    func getWeatherData(city: String) {
        let apiKey = "YOUR_API_KEY"
        let url = "http://api.openweathermap.org/data/2.5/weather?q=\(city)&appid=\(apiKey)"

        Alamofire.request(url).responseJSON { response in
            if let data = response.data {
                let json = JSON(data)
                self.parseWeatherData(json: json)
            }
        }
    }

    func parseWeatherData(json: JSON) {
        let temperature = json["main"]["temp"].doubleValue - 273.15
        let weatherCondition = json["weather"][0]["description"].stringValue

        DispatchQueue.main.async {
            self.temperatureLabel.text = String(format: "%.1f��C", temperature)
            self.weatherConditionLabel.text = weatherCondition
        }
    }
}
